function getWizzard() {
    var xhr = new XMLHttpRequest();

    xhr.open('Get', 'http://localhost:64197/api/wizzard', true);

    xhr.onload = function () {
        var result = JSON.parse(xhr.responseText);

        for(var item in result)  {

            var res = "<tr><td>" + result[item].Id + "</td><td>" + result[item].Name + "</td><td>" + result[item].Species + "</td><td>" + result[item].Gender + "</td><td>" + result[item].House + "</td><td>" + result[item].Ancestry + "</td><td>" + result[item].Patronus + "</td><td>" + result[item].Actor + "</td></tr>";
            document.getElementById("tableWizzard").innerHTML += res;
        };
    };

    xhr.send();
}

